void RsaGenKey_Test(void)
{
	uint32_t u32Ret = 0;                             
	uint8_t au8N[MAX_SIZE];
	uint8_t au8D[MAX_SIZE];
	uint8_t au8E[MAX_SIZE];
	uint8_t au8P[HALF_MAX];
	uint8_t au8Q[HALF_MAX];
	uint8_t au8Dp[HALF_MAX];
	uint8_t au8Dq[HALF_MAX];
	uint8_t au8Qi[HALF_MAX];
	MH_RSA_KEY_SPACE InBase;
	uint8_t u8Base = 0;
	MH_RSA_KEY KeySrc;
	
	DBG_PRINT("RSA_GenKey_Test.\n");
	u8Base = 0;
	memset(&KeySrc,0,sizeof(MH_RSA_KEY));
	memset(&InBase,0,sizeof(MH_RSA_KEY_SPACE));
	
	bn_read_string_from_head((uint8_t *)au8E, sizeof(au8E), RSA_E_2048);
	KeySrc.u16EBits = 32;
	KeySrc.u16NBits = 2048;
	KeySrc.pu8P = au8P;
	KeySrc.pu8Q = au8Q;
	KeySrc.pu8E = au8E;
	KeySrc.pu8Dp = au8Dp;
	KeySrc.pu8Dq = au8Dq;
	KeySrc.pu8Qi = au8Qi;
	KeySrc.u32Crc = MHCRC_CalcBuff(0xffff, &KeySrc, sizeof(MH_RSA_KEY)-4); 
	u32Ret = MHRSA_GenKey(&KeySrc,&InBase,&u8Base,MH_RSA_GENKEY_EFC);
    r_printf((MH_RET_RSA_SUCCESS == u32Ret), "MHRSA_GenKey 2048.\n");
	ouputRes("P.\n", au8P, (KeySrc.u16NBits + 15)>>4);
	ouputRes("Q.\n", au8Q, (KeySrc.u16NBits + 15)>>4);	
	
	KeySrc.pu8N = au8N;
	KeySrc.pu8D = au8D;
	KeySrc.u32Crc = MHCRC_CalcBuff(0xffff, &KeySrc, sizeof(MH_RSA_KEY)-4); 
	u32Ret = MHRSA_CompleteKey(&KeySrc,&InBase,&u8Base,MH_RSA_COMPLETE_CRT);
	r_printf((MH_RET_RSA_SUCCESS == u32Ret), "MHRSA_CompleteKey 2048.\n");
	ouputRes("N.\n", au8N, (KeySrc.u16NBits + 7)>>3);
	ouputRes("D.\n", au8D, (KeySrc.u16NBits + 7)>>3);	
}

